create table KONYV
(
    ID        NUMBER generated as identity,
    SZERZO    VARCHAR2(100),
    CIM       VARCHAR2(100) not null,
    AR        NUMBER        not null,
    OLDALSZAM NUMBER,
    KIADO     VARCHAR2(100),
    ELERESIUT VARCHAR2(200),
    KIADASIEV NUMBER,
    TIPUS     VARCHAR2(100),
    LEIRAS    VARCHAR2(1000)
)
/

create unique index KONYV_ID_UINDEX
    on KONYV (ID)
/

alter table KONYV
    add constraint KONYV_PK
        primary key (ID)
/

INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (53, 'Delilah S. Dawson', 'Pókember: Új kezdet', 940, 80, 'Vad Virágok Kiadó Kft.', null, 2020, 'Magazin', 'Peter Parker felvette már a versenyt veszedelmes gonosztevőkkel és legendás hősökkel is, de lehet, hogy a gimis élet kifog rajta. Szerencsére nincs veszve a remény, sikerrel veheti az akadályokat, ha besegítenek új barátai: Miles Morales és Gwen Stacy. Az Új kezdet tökéletes alkalom arra, hogy a fiatalabb olvasók is belegabalyodjanak Pókember és hihetetlen barátai vadonatúj kalandjainak szövevényes hálójába Delilah S. Dawson író és Fico Ossio rajzoló tolmácsolásában.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (54, 'Robbie Thompson', 'Star Wars: A célpont: Vader', 5025, 136, 'SZUKITS KÖNYVKIADÓ ÉS KÖNYVKER', null, 2020, 'Magazin', 'A Sith sötét nagyura egy titokzatos bűnöző szindikátus után nyomoz, amely a Birodalom határán túl működik. Arról azonban sejtelme sincs, hogy eközben ő maga is célponttá válik: a bűnszövetkezet a galaxis legveszedelmesebb fejvadászait bérelte fel, hogy egyszer s mindenkorra végezzenek vele! A bukott birodalmi tiszt, Beilert Valance által vezetett orgyilkosok csapatát semmi sem állíthatja meg abban, hogy begyűjtsék életük legnagyobb trófeáját. Valance és Dengar a tökéletes fegyvert keresik, hogy végezzenek a Sith nagyúrral, és szükségük is lesz rá, amikor Vader sarokba szorítja őket! De mi lesz, ha az üldözött és az üldözők is felfedik a Rejtett Kéz néven ismert szindikátus sötét titkait?');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (21, 'Karinthy Frigyes', 'Tanár úr kérem', 3200, null, null, null, 2021, 'humoros', 'A Tanár úr kérem Karinthy Frigyes (1887-1938) egyik legnépszerűbb műve, amelyben humoros és szórakoztató stílusban mesél gyermekkoráról és diákéveiről. Ezen a kiadványon Mácsai Pál, a Kossuth- és Jászai Mari-díjas színész előadásában hallhatjuk.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (22, 'Sir ARTHUR CONAN DOYLE', 'Sherlock Holmes kalandjai I.', 4525, 360, 'TITIS TANÁCSADÓ KFT.', null, 2020, 'crimi', 'A "Sherlock Holmes kalandjai" Doyle első novelláskötete, mely a két regény, "A brixtoni rejtély" és "A négyek jele" után jelent meg, 1892-ben. Dr. Watson olyan rejtélyeket válogatott itt csokorba, melyek megoldása ékesen bizonyítja Holmes rendkívüli következtető képességét. Ezek a történetek tették a nagy detektívet igazán közismertté. Hol rejtette el a hölgy a Csehország királya számára oly kompromittáló fényképet? Miért szűnt meg a Rőt Liga? Hová tűnt a vőlegény a templomba vonulás előtt? Lehet-e hinni a meggyilkolt férfi fiának? Mit jelent az öt narancsmag? És hová lett a példás férj és családapa, miután kiintett feleségének egy kétes hírű kocsma emeleti ablakából? Nem csak remekül szórakozik, de ezekre a kérdésekre is választ kap, aki meghallgatja Fekete Ernő nagyszerű előadásában az első 6 kalandot.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (23, 'Bartha Gábor', 'Matematika feladatgyüjtemény 1', 1360, 450, 'Mozaik', null, 2012, 'Tankönyv', 'Matematikai feladatgyüjtemény.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (24, 'dr. Gerőcs László', 'Matematika 11', 1000, 200, 'Mozaik', null, 2012, 'Tankönyv', 'Matematikai oktatóköny.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (25, 'Jay Asher', 'Tizenhárom okom volt...', 5000, 259, 'Könyvmolyképző Kiadó Kft.', null, 2017, 'Szépirodalom', 'Mikor Clay Jensen hazaér az iskolából, egy különös csomagot talál a verandán, amit neki címeztek. A csomagban néhány kazettára bukkan, amiket Hannah Baker - osztálytársa és titkos szerelme - készített, aki két héttel korábban öngyilkosságot követett el. Hannah elmondja, hogy tizenhárom oka volt annak, hogy úgy döntött, véget vet az életének. Clay az egyik. Ha meghallgatja a kazettákat, megtudja, miért. Clay egész éjszaka ide-oda bolyong a városban Hannah szavait hallgatva. Tanúja lesz a lány fájdalmának, és megtudja az igazságot saját magáról - az igazságot, amivel soha nem akart szembenézni.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (30, 'Matthew McConaughey', 'Zöldlámpa', 4242, 297, '21. Század Kiadó', null, 2020, 'Szépirodalom', 'Hogyan stresszeljek kevesebbet. Hogyan érezzem jól magam. Hogyan bántsam kevésbé az embereket. Hogyan bántsanak engem kevésbé. Hogyan legyek jó ember. Ha az ember tudja, hogyanés mikor boldoguljon az élet kihívásaival – hogyan viszonyuljon relatívan az elkerülhetetlenhez –, akkor élvezheti a sikernek azt az állapotát, amit úgy nevezek, „elkapni a zöldlámpát”. Belátásaim és meglátásaim, ráérzéseim és megérzéseim, lazaságaim és cikiségeim. Brutális finomságok, igazságok és szépségek. Megúszások, lebukások és elhasalások, ahogy az élet akadálypályáján próbáltam szlalomozni. Reményeim szerint ez egy jó ízű orvosság, két aszpirin a betegszoba helyett, űrhajó a Marsra, amihez nem kell pilótaengedély, templomi élmény, amihez nem kell újjászületni, könnyes nevetés. Egy szerelmeslevél. Az élethez. Ez a könyv a zöldlámpákról szól, és arról, hogy a sárga és a piros is előbb-utóbb zöld lesz.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (32, 'Doron Rabinovici', 'A földönkívüliek', 3324, 256, 'MAGVETŐ KFT.', null, 2021, 'Irodalom', 'Lehet, hogy egy olyan társadalomban, amely képes a vágóhídra küldeni embereket, az igaz ember helye a vágóhídon van? A rádióban bemondják a szenzációs hírt: az idegenek megérkeztek a Földre. Az embereken úrrá lesz az izgatottság, az eufória és az ismeretlentől való félelem, ami súlyos zavargásokhoz vezet.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (33, 'Andrzej Sapkowski', 'Vaják I. - The Witcher - Az utolsó kívánság', 3790, 352, 'GABO KÖNYVKIADÓ ÉS KERESK.KFT.', null, 2021, 'Szorakoztato', 'Geralt a vajákok közé tartozik: mágikus képességeinek köszönhetően, amelyeket hosszan tartó kiképzése és egy rejtélyes elixír csak még tovább csiszolt, zseniális és könyörtelen harcos hírében áll. Ugyanakkor nem hétköznapi gyilkos: célpontjai vérszomjas szörnyetegek és aljas fenevadak, amelyek mindenütt hatalmas pusztítást végeznek, és megtámadják az ártatlanokat.
Hiába tűnik azonban valami gonosznak vagy jónak, nem biztos, hogy valóban az - és minden mesében van egy csipetnyi igazság.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (35, 'Amanda Yee', 'Jóbarátok - A hivatalos szakácskönyv', 5192, 201, '21. Század Kiadó', null, 2021, 'Gasztronómia', 'Trombitáld össze a jóbarátokat, és üdvözöljétek együtt ezt a több mint 100 receptet ("Na, mi a helyzet?"), amelyet hőn szeretett tévéműsorotok ihletett. Akár minden hájjal megkent chef vagy, mint Monica Geller, akár most indítasz catering céget, mint Phoebe Buffay, vagy régi vágású haspók vagy, mint Joey Tribbiani, a Jóbarátok - A hivatalos szakácskönyv egy sor különböző receptet kínál a különböző szakmai szinten levő chefeknek. Előételek, főfogások, italok és desszertek - minden egyes fejezet olyan ikonikus csemegéket tartogat, mint Monica Hálabarát Lakomája, Rachel Hagyományos Angol Rakott Krémdesszertje, a Sült Krumpli - csakis Joeynak, a Chandler-féle "Tej, amit elrágsz", Phoebe nagyijának csokitallérja és az elmaradhatatlan Hidratáló Szendvics.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (36, 'Dagmar Von Cramm', 'Babakonyha - Csecsemőkortól kisgyerekkorig', 4360, 192, 'Central Médiacsoport Zrt.', null, 2021, 'Gasztronómia', 'Ínycsiklandó receptek kicsiknek és nagyoknak. Finom kásák és kézzel ehető ételek a legkisebbeknek, egyszerűen elkészíthető baba-mama receptek, és sok más ennivaló, amit a család minden tagja szívesen fogyaszt.
Naprakész ismeretek a téma egyik legnagyobb szakértőjétől, Dagmar von Crammtól. Szoptatási tippek, hozzátáplálás lépésről lépésre, jól kamatoztatható tudás a higiénia, a konyhai ismeretek, az ételintolerancia, az allergia, a cukor és a vega táplálkozás témaköreiből.
Amit tehát ez a könyv nyújt: változatos, kiegyensúlyozott táplálkozást biztosító receptek a kisgyermek egyes életszakaszaihoz igazítva, valamint hasznos válaszok a kicsi fejlődését érintő kérdésekre.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (38, 'Dr. Esche-Belke Susanne - Dr. Kirschner-Brouns Suzann', 'Nők a változókor küszöbén - A hormonális egyensúly titkai', 3514, 320, 'Partvonal Könyvkiadó Kft', null, 2021, 'Életmód', 'A változókor nem a világ vége, hanem új lehetőségek kezdete! Depresszió, ízületi fájdalom, migrén, hangulatingadozás, szapora szívverés, hőhullám, súlygyarapodás - ezek a kényelmetlen és sokszor súlyos tünetek gyakran jelentkeznek a negyven feletti nőknél. Az orvostudomány válasza egyértelmű: a változókor előfutárának tekinthető jelenségek hormonális eredetűek, és már a negyvenes éveik elején pokollá tehetik a nők hétköznapjait. Sajnos nem mindig kezelik megfelelően a problémát: nem ismerik fel az okokat, vagy elvárják a nőktől, hogy "éljenek együtt" a hormonváltozással.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (43, 'Szitnyainé Gottlieb Éva, Maros Judit', 'Kon-Takt 2 Arbeitsbuch', 910, 120, 'Mozaik', null, 2012, 'Tankönyv', 'Német nyelvű munkafüzet');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (39, 'Pierre Rabhi', 'Pénzt akarunk vagy életet?', 1900, 190, 'L'' HARMATTAN KFT.', null, 2014, 'Antik', 'Pierre Rabhi a kortárs ökológiai gondolkodás egyik legnagyszerűbb alakja, aki alapítványai révén három kontinensen van jelen, és tízezrek életébe hozott változást Afrikától Indiáig. Mint Yehudi Menuhin írja róla: Ez az egyszerű életszentségű, tisztán és világosan gondolkodó ember, akinek költői szépségű nyelvezete izzó szenvedélyről tanúskodik, saját verítékével termékenyítette meg a meddő földeket. Ez a kötet filozófiai és bölcseleti írásait gyűjti egybe.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (40, 'Erik Burnham', 'Pókember: Pókvadászat - Marvel akcióhősök', 1192, 72, 'Vad Virágok Kiadó Kft.', null, 2021, 'Magazin', 'Peter Parker, Miles Morales és Gwen Stacy egy csapattá állt össze, és ifjonti kalandozásaiknak semmi sem szabhat határt!
Kivéve persze a házi feladatok tömkelege, a szakmai gyakorlat
a Hírharsonában, valamint egy új, veszélyes ellenfél, aki kiszemelte őket magának: Kraven, a vadász! Hőseink egymás után kerülnek a hálójába, hogy kezdetét vehesse a nagy összecsapás. Lehet, hogy ezzel véget ér Peter, Miles és Gwen pókos pályafutása?
Erik Burnham író és Christoher Jones rajzoló humorban és izgalomban bővelkedő történetéből kiderül.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (41, 'Phil Noto - Greg Pak', 'Star Wars: A lázadók végzete', 4232, 128, 'SZUKITS KÖNYVKIADÓ ÉS KÖNYVKER', null, 2021, 'Magazin', 'Lázadók és zsiványok népesítik be a képregény oldalait, melyeken történetünk lassan a végéhez közeledik. A Felkelők Szövetségének hősei merész küldetésekre indultak, hogy zavart keltsenek birodalomszerte. De ki az a Dar Champion, és mennyire bízhat meg benne Leia hercegnő, amikor Han Solo élete a tét? Milyen megrázó élményben lesz része Luke Skywalkernek, amikor Warba végül felfedi valódi kilétét? Képes lesz C-3PO meghozni a végső áldozatot, amikor lehetetlen döntés elé kerül? Csubakka vajon állni fogja a sarat a Darth Vaderrel szembeni ádáz közelharc során?');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (42, 'Sean Murphy', 'Batman - A Fehér Lovag átka', 6796, 272, 'FUMAX KFT', null, 2021, 'Magazin', 'Joker szövetkezik Azraellel, hogy segítsen neki leleplezni a Wayne család hagyatékának döbbenetes titkát - és nem mellékesen tönkretenni Gothamet. Batman a város és veszélyben lévő szerettei segítségére siet, ám miután őseinek titka napvilágra kerül, kénytelen megkérdőjelezni mindent, amit eddig önmagáról és a Gotham életében betöltött szerepéről hitt.
');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (44, 'Szitnyainé Gottlieb Éva, Maros Judit', 'Kon-Takt 2 Lehrbuch', 1460, 112, 'Mozaik', null, 2012, 'Tankönyv', 'Német nyelvű tankönyv');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (45, ' Antalné dr. Szabó Ágnes, dr. Raátz Judit', 'Magyar nyelv és kommunikáció. Tankönyv a 11. évfolyam számára', 790, 68, 'Mozaik', null, 2012, 'Tankönyv', 'Magyar nyelvtankönyv');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (46, 'David Attenborough ', 'Egy ifjú természettudós történetei II. - A sárkány nyomában - Hangoskönyv', 3230, 290, 'Kossuth Kiadó / Mojzer Kiadó', null, 2021, 'Szépirodalom', 'Sir David Attenborough távoli tájakról, állatokról és kultúrákról szóló lebilincselő történetein már több generációnyi lelkes természetkedvelő és természetvédő nőtt fel. Hangoskönyvünk egy indonéziai expedíció kalandjait mutatja be. Most sem alakul minden a terv szerint, adódhattak olyan helyzetek, hogy a csapatnak a sziget egyetlen kamionosát kellett megtalálniuk, a környék egyetlen dzsipjét kölcsönkérniük, vagy éppen heteket várni egy-egy hajós vagy repülős szállításra. Ezeket a helyzeteket és a belőlük adódó bosszantóan komikus szituációkat a szerző jó érzékkel, humorosan mutatja be, érzékeltetve velünk, hogy azért a trópusokon se kolbászból van a kerítés.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (47, 'Sir ARTHUR CONAN DOYLE', 'A sátán kutyája - Hangoskönyv', 3106, null, 'HUNGAROTON', null, 2021, 'Szépirodalom', 'A detektívregény-irodalom klasszikusának, Sir Arthur Conan Doyle-nak (1859 - 1930) egyik legnépszerűbb regényét Szacsvay László előadásában hallhatjuk.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (48, 'Robert Louis Stevenson', 'Dr. Jekyll és Mr. Hyde különös esete - Hangoskönyv', 2793, 212, 'TITIS TANÁCSADÓ KFT.', null, 2020, 'Szépirodalom', 'Stevenson kisregénye a Jekyll-Hyde párost napjainkig az emberben lakozó jó és gonosz szimbólumává tette. Az 1880-as években jelent meg, 1920 és 2000 között számos film készült belőle, majd színdarabok és musical is. Nagy hatását nem csak a kissé hátborzongató leírásnak, a tudomány és a transzcendens gondolkodás ütköztetésének, hanem annak is köszönhető, hogy az emberi lélek működését korát megelőző realitással elemzi. A történetben a gonosz kerekedik fölül, de végül nem győzedelmeskedik, maga is elbukik. Hirtling Isván előadása érzékletesen kiemeli a főhős belső vívódása és a külső szemlélők angolos távolságtartása között mindvégig feszülő ellentétet.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (50, 'Ella Maise', 'Első pillantás - 2019 - Alexander és Maya 1.', 2565, 244, 'ÁLOMGYÁR KIADÓ', null, 2019, 'Erotikus', 'Ez a történet egyedi és imádnivaló! Dayreader
A nevem Maya, és nem ugyanaz a lány vagyok, aki egy hónapja voltam.
Ürességet érzek a mellkasomban. A szívem megszakadt a fájdalomtól.
Azokon a sötét éjszakákon, azokban a fájdalmas pillanatokban, amikor visszaemlékszem, miként szorítottam sikoltva apám hideg, élettelen kezét, nehezemre esik még a lélegzetvétel is, ahogy érzem a veszteség súlyát rám nehezedni.
De minden történetben van egy hős is, ugye? Egy hős, akit attól a pillanattól semmibe vettem, hogy kinyitotta a száját.
A neve Alexander. Ő az elkerülhetetlen, akiről soha nem gondoltam, hogy megtalálom. Az egyetlen, akihez az összetört szívem húz. A lénye, az érintése, minden, amit tett, hogy elviselhetőbbé tegye a fájdalmam, örökké nyomot hagy bennem.');
INSERT INTO VOROS.KONYV (ID, SZERZO, CIM, AR, OLDALSZAM, KIADO, ELERESIUT, KIADASIEV, TIPUS, LEIRAS) VALUES (52, 'Stephen King', 'Kedvencek temetője - 2019', 5700, 587, 'EURÓPA KÖNYVKIADÓ KFT.', null, 2019, 'Szépirodalom', 'Dr. Louis Creed, a fiatal orvos kitűnő állást kapott: a Maine-i Egyetem rendelőjének lett a vezetője, ezért Chicagóból az idilli New England-i tájban álló, magányos házba költözik családjával feleségével, Rachellel, ötéves lányukkal, Ellie-vel és másfél éves kisfiukkal, Gage-dzsel. Boldogan, a szép jövő reményében veszik birtokba új otthonukat Az első gondra az út túloldalán, velük átellenben élő öregember, Jud hívja föl a figyelmüket: a tájat kettészelő országúton éjjel-nappal olajszállító tartálykocsik dübörögnek, halálos veszélynek téve ki a háziállatokat és az apróságokat. Nem véletlenül van a közelben egy nyomasztó légkörű, ódon temető az elgázolt háziállatok számára Az első trauma akkor éri Louist, amikor egy baleset áldozatául esett, haldokló fiú a rendelőben dadogó szavakkal óva inti az állattemetőn túli veszedelemtől.');